import {
  QueryClient,
  QueryClientProvider,
  useQuery,
} from "@tanstack/react-query";
import { ReactQueryDevtools } from "@tanstack/react-query-devtools";
import {
  BrowserRouter,
  createBrowserRouter,
  Route,
  Routes,
} from "react-router-dom";
import Layout from "./Layout/Layout";
import AnimeContainer from "./Home/AnimeContainer";
import AnimeDetails from "./View/AnimeDetails";
import { useState } from "react";

const queryClient = new QueryClient();

async function getData(query) {
  let res;
  if (query !== "") {
    res = await fetch(`https://api.jikan.moe/v4/anime?q=${query}&sfw=true`);
  } else {
    res = await fetch(`https://api.jikan.moe/v4/anime?q&sfw=true`);
  }

  if (!res.ok) {
    throw new Error("Response Was not Okay");
  }

  const data = await res.json();
  return data;
}

export default function App() {
  const [chosen, setChosen] = useState({});
  const [isBack, setIsBack] = useState(true);
  const [query, setQuery] = useState("");
  const { isLoading, data } = useQuery({
    queryKey: ["animeData", query],
    queryFn: () => getData(query),
  });
  function handleIsBack() {
    setIsBack((isBack) => !isBack);
  }

  function handleSearch(queryValue) {
    setIsBack(true);
    setQuery(queryValue);
  }

  function handleChoice(chosenAnime) {
    setChosen(chosenAnime);
    handleIsBack();
  }

  function handleSort(sortType) {
    setIsBack(true);
    switch (sortType) {
      case "name":
        data?.data?.result
          .slice()
          .sort((a, b) => a.title.localeCompare(b.title));
        break;
      case "epi":
        data?.data?.result.slice().sort((a, b) => a?.episodes - b?.episodes);
        break;
      default:
        console.log("something Went Wrong");
    }
  }

  const router = createBrowserRouter([
    {
      element: (
        <Layout
          query={query}
          handleSearch={handleSearch}
          handleSort={handleSort}
        />
      ),
      children: [{}, {}],
    },
  ]);

  return (
    <QueryClientProvider client={queryClient}>
      {/* <ReactQueryDevtools initialIsOpen={false} /> */}

      <BrowserRouter>
        <Routes>
          <Route
            index
            path="/"
            element={
              <AnimeContainer
                isLoading={isLoading}
                data={data}
                handleChoice={handleChoice}
              />
            }
          />
          <Route
            path="/Anime Info"
            element={
              <AnimeDetails chosenAnime={chosen} onBack={handleIsBack} />
            }
          />
        </Routes>
      </BrowserRouter>
    </QueryClientProvider>
  );
}

