import { useState } from "react";
import Loader from "../ui/Loader";
import { useQuery } from "@tanstack/react-query";
import { Outlet } from "react-router-dom";
import Header from "../ui/Header";
import ControlBar from "../Home/ControlBar";
import Card from "../Home/Card";
import AnimeContainer from "../Home/AnimeContainer";
import AnimeDetails from "../View/AnimeDetails";

async function getData(query) {
  let res;
  if (query !== "") {
    res = await fetch(`https://api.jikan.moe/v4/anime?q=${query}&sfw=true`);
  } else {
    res = await fetch(`https://api.jikan.moe/v4/anime?q&sfw=true`);
  }

  if (!res.ok) {
    throw new Error("Response Was not Okay");
  }

  const data = await res.json();
  return data;
}

function Layout() {
  function handleIsBack() {
    setIsBack((isBack) => !isBack);
  }

  function handleSearch(queryValue) {
    setIsBack(true);
    setQuery(queryValue);
  }

  function handleChoice(chosenAnime) {
    setChosen(chosenAnime);
    handleIsBack();
  }

  function handleSort(sortType) {
    setIsBack(true);
    switch (sortType) {
      case "name":
        data?.data?.result
          .slice()
          .sort((a, b) => a.title.localeCompare(b.title));
        break;
      case "epi":
        data?.data?.result.slice().sort((a, b) => a?.episodes - b?.episodes);
        break;
      default:
        console.log("something Went Wrong");
    }
  }

  return (
    <div className="container">
      <Header />
      <div className="anime">
        <ControlBar
          search={query}
          onSearch={handleSearch}
          onSort={handleSort}
        />
        <Outlet />

        {/* {isBack ? (
          <AnimeContainer>
            {isLoading ? (
              <Loader />
            ) : (
              data?.data?.map((el, key) => (
                <Card animeData={el} onChoice={handleChoice} key={key} />
              ))
            )}
          </AnimeContainer>
        ) : (
          <AnimeDetails chosenAnime={chosen} onBack={handleIsBack} />
        )} */}
      </div>
    </div>
  );
}

export default Layout;
