//     res = await fetch(`https://api.jikan.moe/v4/anime?q&sfw=true`);
//   }

//   if (!res.ok) {
//     throw new Error("Response Was not Okay");
//   }

//   const data = await res.json();
//   return data;
// }

async function getData(query, currentPage = 1) {
  let res;
  if (query !== "") {
    res = await fetch(`https://api.jikan.moe/v4/anime?q=${query}&sfw=true&
      page=${currentPage}`);
  } else {
    res = await fetch(
      `https://api.jikan.moe/v4/seasons/now?sfw=true&currentPage=${currentPage}`
    );
  }

  if (!res.ok) {
    throw new Error("Response Was not Okay");
  }

  const data = await res.json();
  return data;
}

const AnimeContext = createContext("animeContext");

export default function AnimeProvider({ children }) {
  const [chosen, setChosen] = useState({});
  const [query, setQuery] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const { isLoading, data } = useQuery({
    queryKey: ["animeData", query, currentPage],
    queryFn: () => getData(query, currentPage),
  });

  // const currentpage = data?.pagination?.current_page || 1;
  const lastPage = data?.pagination?.last_visible_page || 1;
  const totalItems = data?.pagination?.items?.total || 0;

  function handlePageClick() {
    if (data?.pagination?.has_next_page) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  }

  function handleSearch(queryValue) {
    setQuery(queryValue);
  }

  function handleChoice(chosenAnime) {
    setChosen(chosenAnime);
  }

  function handleSort(sortType) {
    switch (sortType) {
      case "name":
        data?.data?.result
          ?.slice()
          .sort((a, b) => a.title.localeCompare(b.title));
        break;
      case "epi":
        data?.data?.result.slice().sort((a, b) => a?.episodes - b?.episodes);
        break;
      default:
        console.log("something Went Wrong");
    }
  }

  console.log(data);

  const value = {
    handleSearch,
    handleChoice,
    handleSort,
    query,
    isLoading,
    data,
    chosen,
    currentPage,
    lastPage,
    totalItems,
  };

  return (
    <AnimeContext.Provider value={value}>{children}</AnimeContext.Provider>
  );
}

export function useAnime() {
  const value = useContext(AnimeContext);

  if (!value) {
    throw new Error("You Are Using The Context Outside The Provider");
  }

  return value;
}
