function spreadArray(chosenAnime, myArray) {
  return chosenAnime[myArray]
    ?.map((el) => el)
    ?.map((el) => el?.name)
    ?.join(" , ");
}

export function Aside({ chosenAnime }) {
  const producers = spreadArray(chosenAnime, "producers");
  const licensors = spreadArray(chosenAnime, "licensors");
  const studios = spreadArray(chosenAnime, "studios");
  const themes = spreadArray(chosenAnime, "demographics");
  const demographics = spreadArray(chosenAnime, "themes");

  return (
    <section className="aside-item-box glass">
      <span>
        Title: <strong>{chosenAnime?.title}</strong>
      </span>

      <span>
        Title: <strong>{chosenAnime?.title}</strong>
      </span>

      <span>Type: {chosenAnime?.type || " N/A"}</span>

      <span>Episodes: {chosenAnime?.episodes || " N/A"}</span>

      <span>Status: {chosenAnime?.status || " N/A"}</span>

      <span>Aired: {chosenAnime?.aired?.string || " N/A"}</span>

      <span>Producers: {producers || " N/A"}</span>

      <span>Licensors:{licensors || " N/A"}</span>

      <span>Studios: {studios || " N/A"}</span>

      <span>Source: {chosenAnime?.source || " N/A"}</span>

      <span>Theme: {themes || " N/A"}</span>

      <span>Demographic: {demographics || " N/A"}</span>

      <span>Duration: {chosenAnime?.duration || " N/A"}</span>

      <span>Rating: {chosenAnime?.rating || " N/A"}</span>
    </section>
  );
}
